import TerminosModal from "./TerminosModal";
import React, {useState} from "react";
import {Formik, useFormik, Form, FormikConfig, FormikValues} from "formik";
import * as Yup from 'yup';
import {rutValidador} from "../utils/validationRut";

const APVForm = () => {

    const [currentStep, setCurrentStep] = useState(0);
    const [lastStep, setLastStep] = useState(2);

    const aacento = "\u00e1";
    const eacento = "\u00e9";
    const iacento = "\u00ed";
    const oacento = "\u00f3";
    const uacento = "\u00fa";

    const prevStep = () =>{
        setCurrentStep(currentStep - 1);
    }

    const nextStep = props => {
        props.submitForm().then(()=> {
            if(props.isValid) {
                setCurrentStep(currentStep + 1);
                props.validateForm();
                props.setTouched({});
            }
        })
    }

    const initialValues = {
        nombre: '',
        rut: '',
        correo: '',
        celular: '',
        sueldo: '',
        ahorro: '',
        terminosycondiciones: false
    }

    const onSubmit = values => {
        console.log('Form values', values);
    }

    const Step1Schema = Yup.object({
        sueldo: Yup
            .string()
            .matches(/^[0-9]{6,8}$/, `Ingrese un sueldo l${iacento}quido entre $100.000 y $10.000.000.`)
            .test('Sueldo-validacion', `Ingrese un sueldo l${iacento}quido entre $100.000 y $10.000.000.`, function (value) {
                let amount = formik.values.sueldo;
                return (amount >= 100000 && amount <= 10000000)
            })
            .required('Por favor ingrese su sueldo líquido.'),
        ahorro: Yup
            .string()
            .matches(/^[0-9]+$/, `Ingrese el monto en pesos que desea ahorrar desde $1.000.`)
            .test('Sueldo-validacion', `Ingrese un monto desde $1.000.`, function (value) {
                let amount = formik.values.sueldo;
                return (amount >= 1000 )
            })
            .required('Por favor ingrese el monto que desea ahorrar desde $1.000.')
    });

    const Step2Schema = Yup.object({
        nombre: Yup
            .string()
            .matches(
                /^[A-Za-z \u00F1\u00D1\u00E1\u00E9\u00ED\u00F3\u00FA\u00FC]{3,60}$/,
                `Ingresa tu nombre sin n${uacento}meros ni caracteres.`)
            .required('Ingresa al menos un nombre y apellido.'),
        rut: Yup
            .string()
            .required(`Por favor ingrese un RUT v${aacento}lido sin puntos y con gui${oacento}n.`)
            .test('Rut-validacion',
                `Por favor ingrese un RUT v${aacento}lido sin puntos y con gui${oacento}n.`,
                function (value) {
                    return rutValidador(formik.values.rut);
                })
    });

    const Step3Schema = Yup.object({
        correo: Yup
            .string()
            .email(`Tu correo debe incluir un "@" y un dominio v${aacento}lido.`)
            .required('Por favor ingrese su correo.'),
        celular: Yup
            .string()
            .matches(/^[9]{1}[0-9]{8}$/, `Ingresa un n${uacento}mero de 9 d${iacento}gitos.`)
            .required(`Por favor ingrese su n${uacento}mero de tel${eacento}fono.`),
        terminosycondiciones: Yup
            .boolean()
            .oneOf([true], `Debe aceptar las condiciones del servicio y la pol${iacento}tica de privacidad.`)
    });

    const validationSchema = Yup.object({
        nombre: Yup
            .string()
            .matches(
                /^[A-Za-z \u00F1\u00D1\u00E1\u00E9\u00ED\u00F3\u00FA\u00FC]{3,60}$/,
                `Ingresa tu nombre sin n${uacento}meros ni caracteres.`)
            .required('Ingresa al menos un nombre y apellido.'),
        rut: Yup
            .string()
            .required(`Por favor ingrese un RUT v${aacento}lido sin puntos y con gui${oacento}n.`)
            .test('Rut-validacion',
                `Por favor ingrese un RUT v${aacento}lido sin puntos y con gui${oacento}n.`,
                function (value) {
                    return rutValidador(formik.values.rut);
                }),
        correo: Yup
            .string()
            .email(`Tu correo debe incluir un "@" y un dominio v${aacento}lido.`)
            .required('Por favor ingrese su correo.'),
        celular: Yup
            .string()
            .matches(/^[9]{1}[0-9]{8}$/, `Ingresa un n${uacento}mero de 9 d${iacento}gitos.`)
            .required(`Por favor ingrese su n${uacento}mero de tel${eacento}fono.`),
        sueldo: Yup
            .string()
            .matches(/^[0-9]{6,8}$/, `Ingrese un sueldo l${iacento}quido entre $100.000 y $10.000.000.`)
            .test('Sueldo-validacion', `Ingrese un sueldo l${iacento}quido entre $100.000 y $10.000.000.`, function (value) {
               let amount = formik.values.sueldo;
                return (amount >= 100000 && amount <= 10000000)
            })
            .required('Por favor ingrese su sueldo líquido.'),
        ahorro: Yup
            .string()
            .matches(/^[0-9]+$/, `Ingrese el monto en pesos que desea ahorrar desde $1.000.`)
            .test('Sueldo-validacion', `Ingrese un monto desde $1.000.`, function (value) {
                let amount = formik.values.sueldo;
                return (amount >= 1000 )
            })
            .required('Por favor ingrese el monto que desea ahorrar desde $1.000.'),
        terminosycondiciones: Yup
            .boolean()
            .oneOf([true], `Debe aceptar las condiciones del servicio y la pol${iacento}tica de privacidad.`)

    });


    const formik = useFormik({
       initialValues,
       onSubmit,
       validationSchema
    });

    const [modalShow, setModalShow] = useState(false);
    const handleClose = () => setModalShow(false);
    const handleShow = () => setModalShow(true);

    return (
        <Formik
            initialValues={initialValues}
            validationSchema={validationSchema}
            onSubmit={onSubmit}
        >
            <Form>
                <div>
                    <div className="form-group input-wrapper">
                        <p>Ingresa tus datos y lo calcularemos para ti:</p>
                        <input
                            type="text"
                            className={`
                            form-control 
                            form-control-lg 
                            ${formik.touched.sueldo ? (formik.errors.sueldo ? "is-invalid" : "is-valid") : ""}`}
                            id="sueldo"
                            name="sueldo"
                            aria-describedby="sueldoAyuda"
                            placeholder="Sueldo Líquido"
                            {...formik.getFieldProps('sueldo')}
                        />
                        <label
                            htmlFor="sueldo"
                            className={"control-label"}
                        >
                            Sueldo Liquido
                        </label>
                        <small
                            id="sueldoAyuda"
                            className={`form-text 
                        ${formik.touched.sueldo && formik.errors.sueldo ? "is-invalid" : "" }`}
                        >
                            {formik.touched.sueldo && formik.errors.sueldo
                                ? formik.errors.sueldo
                                : "El sueldo líquido que recibes luego de los descuentos legales."}
                        </small>
                    </div>
                    <div className="form-group input-wrapper">
                        <input
                            type="text"
                            className={`form-control form-control-lg ${
                                formik.touched.ahorro ? (formik.errors.ahorro ? "is-invalid" : "is-valid") : ""}`}
                            id="ahorro"
                            name="ahorro"
                            aria-describedby="ahorroAyuda"
                            placeholder="Ahorro Mensual"
                            {...formik.getFieldProps('ahorro')}
                        />
                        <label
                            htmlFor="ahorro"
                            className="control-label"
                        >
                            Monto Ahorro
                        </label>
                        <small
                            id="ahorroAyuda"
                            className={`form-text ${
                                formik.touched.ahorro && formik.errors.ahorro ? "is-invalid" : ""}`}
                        >
                            {formik.touched.ahorro && formik.errors.ahorro
                                ? formik.errors.ahorro
                                : "El monto en pesos que invertirías en tu APV."}
                        </small>
                    </div>
                </div>

                <div>
                    <div className="form-group input-wrapper">
                        <input
                            type="text"
                            className={`form-control form-control-lg ${
                                formik.touched.nombre ? (formik.errors.nombre ? "is-invalid" : "is-valid" ) : ""}`}
                            id="nombre"
                            name="nombre"
                            aria-describedby="nombreAyuda"
                            placeholder="Nombre"
                            {...formik.getFieldProps('nombre')}
                        />
                        <label
                            htmlFor="nombre"
                            className="control-label"
                        >
                            Nombre
                        </label>
                        <small
                            id="nombreAyuda"
                            className={`form-text ${
                                formik.touched.nombre && formik.errors.nombre ? "is-invalid" : ""
                            }`}
                        >
                            {formik.touched.nombre && formik.errors.nombre
                                ? formik.errors.nombre
                                : "Indicanos tu nombre y apellido."}
                        </small>
                    </div>
                    <div className="form-group input-wrapper">
                        <input
                            type="text"
                            className={`form-control form-control-lg ${
                                formik.touched.rut ? (formik.errors.rut ? "is-invalid" : "is-valid") : ""}`}
                            id="rut"
                            name="rut"
                            aria-describedby="rutAyuda"
                            placeholder="RUT"
                            {...formik.getFieldProps('rut')}
                        />
                        <label
                            htmlFor="rut"
                            className="control-label"
                        >
                            RUT
                        </label>
                        <small
                            id="rutAyuda"
                            className={`form-text ${
                                formik.touched.rut && formik.errors.rut ? "is-invalid" : ""}`}
                        >
                            {formik.touched.rut && formik.errors.rut
                                ? formik.errors.rut
                                : "Ej. 12345678-9."}
                        </small>
                    </div>
                </div>

                <div>
                    <div className="form-group input-wrapper">
                        <input
                            type="email"
                            className={`form-control form-control-lg ${
                                formik.touched.correo ? (formik.errors.correo ? 'is-invalid' : 'is-valid') : ''
                            }`}
                            id="correo"
                            name="correo"
                            aria-describedby="correoAyuda"
                            placeholder="Correo"
                            {...formik.getFieldProps('correo')}
                        />
                        <label
                            htmlFor="correo"
                            className="control-label"
                        >
                            Correo
                        </label>
                        <small
                            id="correoAyuda"
                            className={`form-text ${
                                formik.touched.correo && formik.errors.correo ? "is-invalid" : ""}`}
                        >
                            {formik.touched.correo && formik.errors.correo
                                ? formik.errors.correo
                                : "Ej. aumentatusueldo@afpmodelo.cl"}
                        </small>
                    </div>
                    <div className="form-group input-wrapper">
                        <input
                            type="text"
                            className={`form-control form-control-lg ${
                                formik.touched.celular ? (formik.errors.celular ? "is-invalid" : "is-valid") : ""}`}
                            id="celular"
                            name="celular"
                            aria-describedby="celularAyuda"
                            placeholder="Celular"
                            {...formik.getFieldProps('celular')}
                        />
                        <label
                            htmlFor="celular"
                            className="control-label"
                        >
                            Celular
                        </label>
                        <small
                            id="celularAyuda"
                            className={`form-text ${
                                formik.touched.celular && formik.errors.celular ? "is-invalid" : ""}`}
                        >
                            {formik.touched.celular && formik.errors.celular ? formik.errors.celular : "Ej. 9111223XX"}
                        </small>
                    </div>

                    <div className="form-check">
                        <label
                            className="form-check-label mt-auto"
                            htmlFor="terminosycondiciones"
                        >
                            <input
                                type="checkbox"
                                name="terminosycondiciones"
                                className="form-check-input"
                                {...formik.getFieldProps('terminosycondiciones')}
                            />
                            <small className="terminosycondiciones">
                                Acepto las{" "}
                                <a
                                    onClick={handleShow}
                                >
                                    condiciones del servicio y la pol{iacento}
                                    tica de privacidad.
                                </a>
                            </small>
                        </label>
                        <small
                            id="terminosAyuda"
                            className={
                                `form-text 
                            ${ formik.touched.terminosycondiciones && formik.errors.terminosycondiciones
                                    ? 'is-invalid': ''}`}
                        >
                            {formik.touched.terminosycondiciones && formik.errors.terminosycondiciones
                                ? formik.errors.terminosycondiciones
                                : ""}
                        </small>
                    </div>
                </div>

                <div className="d-flex justify-content-center">
                    <div className="col justify-content-center d-flex">
                        <button
                            type="submit"
                            id="calcular"
                            className="btn btn-lg btn-block"
                            disabled={!(formik.isValid && formik.dirty)}
                        >
                            Calcular
                        </button>
                        <TerminosModal
                            show={modalShow}
                            onHide={handleClose}
                        />
                    </div>
                </div>
            </Form>
        </Formik>
    );
};


export default APVForm;
