import Head from 'next/head'
import ahorro from "../public/assets/svg/ahorro.svg"
import React, { useEffect, useState } from "react";
import TerminosModal from "../components/TerminosModal";
import useForm from "../utils/useForm";
import validate from "../utils/validateForm";
import {Card} from "react-bootstrap";
import ampolleta from "../public/assets/svg/ampolleta.svg";


export default function Home() {

    const [modalShow, setModalShow] = useState(false);
    const [checbox, setChecbox] = useState(false);
    const handleClose = () => setModalShow(false);
    const handleShow = () => setModalShow(true);
    const iacento = "\u00ed";

    if (typeof window !== "undefined") {
        localStorage.setItem("sueldoAnual", 0);
    }


    async function submit() {

        const body = {
            Nombre: form.nombre,
            email: form.correo,
            Rut: form.rut,
            CodAfpOrigen: form.afp,
            Sueldo: eliminarValoresNoEnteros(form.sueldo),
            FonoMovil: form.celular
        };

        console.log(body);
        /**
         const headers = {
            "Content-Type": "application/json"
        };

        axios.post(`Prospectos`, body, { headers: headers }).then((response) => {
            let data = response.data;

            const rutLimpio = form.rut.replace(/[^0-9kK-]/g, "");

            localStorage.setItem("sueldoAnual", data.sueldoAnual);
            localStorage.setItem("sessionId", uuidv4());
            localStorage.setItem("rut", rutLimpio);

            const body_eventos = {
                Sesion_id: localStorage.getItem("sessionId"),
                Evento_id: 0,
                Result_0: parseInt(data.sueldoAnual),
                Result_1: 0,
                Result_2: 0,
                Rut: rutLimpio
            };

            axios
                .post('Eventos', body_eventos, { headers: headers })
                .then((response) => {
                    let data = response.data;

                    if (data.resultado) {
                        window.location.href = "/resultado";
                    }
                })
                .catch(e => {
                    console.log(e);
                });

        });
         **/
    }

    const { handleChange, handleSubmit, form, errors } = useForm(submit, validate);

    function handleInput(e) {
        const value = e.target.checked;
        setChecbox(value);
    }

    useEffect(() => {
        form.esDisabled=validador();
    }, [form]);

    function validador() {
        if (checbox === true && (errors.nombre !== undefined && errors.nombre === "") && (errors.afp !== undefined && errors.afp === "") && (errors.rut !== undefined && errors.rut === "") && (errors.correo !== undefined && errors.correo === "") && (errors.celular !== undefined && errors.celular === "") && (errors.sueldo !== undefined && errors.sueldo === "") ) {
            form.terminosycondiciones = checbox;
            return false;
        } else {
            return true;
        }
    }

  return (
    <div className="container">
      <Head>
        <title>Aumenta tu Sueldo | Solicitud de Contacto | AFP Modelo</title>
        <meta name="description" content="Aumenta tu sueldo l√≠quido, pagando una menor comisi√≥n de AFP. Simula tu aumento de sueldo al cambiarte a AFP Modelo." />
        <meta name="robots" content="noindex, follow" />
      </Head>
        <div className="background">
            <section>
                <div className="row w-100 container-padre calculadora">

                    <div className="col-12 d-flex justify-content-center mobile">
                        <div className="container-title">
                            <h1>Conoce cu√°nto</h1>
                            <h1>aumentar√≠a tu sueldo,</h1>
                            <h1>al cambiarte a</h1>
                            <h1>AFP Modelo.</h1>
                            <p>
                                √önete a la AFP con una de las comisiones m√°s bajas del mercado.
                            </p>
                            <br/>
                            <br/>
                            <img src={ahorro} alt="cerditoAhorro" />
                        </div>
                    </div>
                    <div className="col-md-5 bloque desktop">
                        <img src={ampolleta} alt="pareja modelo" />
                        <h1>¬øPor qu√© abrir un APV?</h1>
                        <p>El APV te permite complementar tus ahorros y/o compensar per√≠odos no cotizados, para mejorar tu pensi√≥n. Dependiendo del r√©gimen tributario que elijas podr√°s recibir un aporte fiscal o rebajar lo ahorrado de tu renta tributable.</p>
                    </div>
                    <div className="col-sm-12 col-md-5 bloque desktop">
                        <div className="txtDesktop">
                            <div className="container-title">
                                <h1>Conoce cu√°nto</h1>
                                <h1>aumentar√≠a tu</h1>
                                <h1>sueldo,</h1>
                                <h1 className="green">al cambiarte a</h1>
                                <h1 className="green">AFP Modelo.</h1>
                                <p>
                                    √önete a la AFP con una de las comisiones m√°s bajas del
                                    mercado.
                                </p>
                            </div>
                        </div>
                    </div>
                    <div className="col-sm-12  col-md-5 bloque form">
                        <div className="card formulario">
                            <div className="card-body">
                                <form onSubmit={handleSubmit}>
                                    <div className="form-group input-wrapper">
                                        <p>Ingresa tus datos y lo calcularemos para ti:</p>
                                        <input
                                            type="text"
                                            className={`form-control form-control-lg ${
                                                errors.nombre !== undefined
                                                    ? errors.nombre !== ""
                                                    ? "is-invalid"
                                                    : "is-valid"
                                                    : ""
                                            }`}
                                            id="nombre"
                                            name="nombre"
                                            aria-describedby="nombreAyuda"
                                            placeholder="Nombre"
                                            onChange={handleChange}
                                            value={form.nombre}
                                        />
                                        <label
                                            htmlFor="nombre"
                                            className={`control-label ${errors.nombre !== undefined
                                                ? errors.nombre.length > 40
                                                    ? "label-mobile-error"
                                                    : ""
                                                : ""}`}
                                        >
                                            Nombre
                                        </label>
                                        <small
                                            id="nombreAyuda"
                                            className={`form-text ${
                                                errors.nombre !== undefined &&
                                                errors.nombre !== ""
                                                    ? "is-invalid"
                                                    : ""
                                            }`}
                                        >
                                            {errors.nombre !== undefined && errors.nombre !== ""
                                                ? errors.nombre
                                                : "Indicanos tu nombre y apellido."}
                                        </small>
                                    </div>
                                    <div className="form-group input-wrapper">
                                        <input
                                            type="text"
                                            className={`form-control form-control-lg ${
                                                errors.rut !== undefined
                                                    ? errors.rut !== ""
                                                    ? "is-invalid"
                                                    : "is-valid"
                                                    : ""
                                            }`}
                                            id="rut"
                                            name="rut"
                                            aria-describedby="rutAyuda"
                                            placeholder="RUT"
                                            onChange={handleChange}
                                            value={form.rut}
                                        />
                                        <label
                                            htmlFor="rut"
                                            className={`control-label ${errors.rut !== undefined
                                                ? errors.rut.length > 40
                                                    ? "label-mobile-error"
                                                    : ""
                                                : ""}`}
                                        >
                                            RUT
                                        </label>
                                        <small
                                            id="rutAyuda"
                                            className={`form-text ${
                                                errors.rut !== undefined && errors.rut !== ""
                                                    ? "is-invalid"
                                                    : ""
                                            }`}
                                        >
                                            {errors.rut !== undefined && errors.rut !== ""
                                                ? errors.rut
                                                : "Ej. 12345678-9."}
                                        </small>
                                    </div>
                                    <div className="form-group input-wrapper">
                                        <input
                                            type="email"
                                            className={`form-control form-control-lg ${
                                                errors.correo !== undefined
                                                    ? errors.correo !== ""
                                                    ? "is-invalid"
                                                    : "is-valid"
                                                    : ""
                                            }`}
                                            id="correo"
                                            value={form.correo}
                                            name="correo"
                                            aria-describedby="correoAyuda"
                                            placeholder="Correo"
                                            onChange={handleChange}
                                        />
                                        <label
                                            htmlFor="correo"
                                            className={`control-label ${errors.correo !== undefined
                                                ? errors.correo.length > 40
                                                    ? "label-mobile-error"
                                                    : ""
                                                : ""}`}
                                        >
                                            Correo
                                        </label>
                                        <small
                                            id="correoAyuda"
                                            className={`form-text ${
                                                errors.correo !== undefined &&
                                                errors.correo !== ""
                                                    ? "is-invalid"
                                                    : ""
                                            }`}
                                        >
                                            {errors.correo !== undefined && errors.correo !== ""
                                                ? errors.correo
                                                : "Ej. aumentatusueldo@afpmodelo.cl"}
                                        </small>
                                    </div>
                                    <div className="form-group input-wrapper">
                                        <input
                                            type="text"
                                            className={`form-control form-control-lg ${
                                                errors.celular !== undefined
                                                    ? errors.celular !== ""
                                                    ? "is-invalid"
                                                    : "is-valid"
                                                    : ""
                                            }`}
                                            id="celular"
                                            name="celular"
                                            aria-describedby="celularAyuda"
                                            placeholder="Celular"
                                            value={form.celular}
                                            onChange={handleChange}
                                        />
                                        <label
                                            htmlFor="celular"
                                            className={`control-label ${errors.celular !== undefined
                                                ? errors.celular.length > 40
                                                    ? "label-mobile-error"
                                                    : ""
                                                : ""}`}
                                        >
                                            Celular
                                        </label>
                                        <small
                                            id="celularAyuda"
                                            className={`form-text ${
                                                errors.celular !== undefined &&
                                                errors.celular !== ""
                                                    ? "is-invalid"
                                                    : ""
                                            }`}
                                        >
                                            {errors.celular !== undefined &&
                                            errors.celular !== ""
                                                ? errors.celular
                                                : "Ej. 9111223XX"}
                                        </small>
                                    </div>
                                    <div className="form-group input-wrapper">
                                        <input
                                            type="text"
                                            className={`form-control form-control-lg ${
                                                errors.sueldo !== undefined
                                                    ? errors.sueldo !== ""
                                                    ? "is-invalid"
                                                    : "is-valid"
                                                    : ""
                                            }`}
                                            id="sueldo"
                                            name="sueldo"
                                            aria-describedby="sueldoAyuda"
                                            placeholder="Sueldo L√≠quido"
                                            value={form.sueldo}
                                            onChange={handleChange}
                                        />
                                        <label
                                            htmlFor="sueldo"
                                            className={`control-label ${errors.sueldo !== undefined
                                                ? errors.sueldo.length > 40
                                                    ? "label-mobile-error"
                                                    : ""
                                                : ""} ${errors.sueldo !== undefined && errors.sueldo !== ""
                                                ? ""
                                                : "label-mobile-error"
                                            }
                                                    `}
                                        >
                                            Sueldo Liquido
                                        </label>
                                        <small
                                            id="sueldoAyuda"
                                            className={`form-text ${
                                                errors.sueldo !== undefined &&
                                                errors.sueldo !== ""
                                                    ? "is-invalid"
                                                    : ""
                                            }`}
                                        >
                                            {errors.sueldo !== undefined && errors.sueldo !== ""
                                                ? errors.sueldo
                                                : "El sueldo l√≠quido que recibes luego de los descuentos legales."}
                                        </small>
                                    </div>
                                    <div className="form-group input-wrapper select">
                                        <label htmlFor="afp">
                                            Ind√≠canos tu AFP actual:
                                            <select
                                                className="form-control form-control-lg"
                                                id="afp"
                                                name="afp"
                                                onChange={handleChange}
                                                value={form.afp}
                                            >
                                                {" "}
                                                {/* eslint-disable-line */}
                                                <option value="default">
                                                    Seleccione una AFP
                                                </option>
                                                <option value="0">AFP Capital</option>
                                                <option value="1">AFP Cuprum</option>
                                                <option value="2">AFP Habitat</option>
                                                <option value="3">AFP Plan Vital</option>
                                                <option value="4">AFP Provida</option>
                                                <option value="6">AFP Uno</option>
                                            </select>
                                        </label>
                                        <small
                                            id="afpAyuda"
                                            className={`form-text ${
                                                errors.afp !== undefined && errors.afp !== ""
                                                    ? "is-invalid"
                                                    : ""
                                            }`}
                                        >
                                            {errors.afp !== undefined && errors.afp !== ""
                                                ? errors.afp
                                                : ""}
                                        </small>
                                    </div>
                                    <div className="form-check">
                                        <label
                                            className="form-check-label mt-auto"
                                            htmlFor="terminosycondiciones"
                                        >
                                            <input
                                                type="checkbox"
                                                name="terminosycondiciones"
                                                onChange={handleInput}
                                                className="form-check-input"

                                            />
                                            <small className="terminosycondiciones">
                                                Acepto las{" "}
                                                <a
                                                    onClick={handleShow}
                                                >
                                                    condiciones del servicio y la pol{iacento}
                                                    tica de privacidad.
                                                </a>
                                            </small>
                                        </label>
                                        {errors.terminosycondiciones !== undefined ? (
                                            <small
                                                id="terminosAyuda"
                                                className={
                                                    errors.terminosycondiciones !== ""
                                                        ? "form-text is-invalid"
                                                        : "form-text"
                                                }
                                            >
                                                {errors.terminosycondiciones !== ""
                                                    ? errors.terminosycondiciones
                                                    : " "}
                                            </small>
                                        ) : (
                                            " "
                                        )}
                                    </div>
                                    <div className="d-flex justify-content-center">
                                        <div className="col justify-content-center d-flex">
                                            <button
                                                type="submit"
                                                id="calcular"
                                                className="btn btn-lg btn-block"
                                                disabled={validador()}

                                            >
                                                Calcular
                                            </button>

                                            <TerminosModal
                                                show={modalShow}
                                                onHide={handleClose}
                                            />
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>

                </div>
            </section>
            <section className="">
                <div className="row">
                    <h4>Existen dos r√©gimenes de APV</h4>
                </div>
                <div className="row">
                    <div className="col-sm-12 col-lg-6">
                        <Card>
                            <Card.Header>R√©gimen A</Card.Header>
                            <Card.Body>
                                <Card.Text>
                                    <p>
                                        En este el Estado te entrega una bonificaci√≥n de un 15% de lo que ahorras en el a√±o con un tope de 6 UTM anuales. Por ejemplo, si ahorras $100.000 recibir√°s $15.000 adicionales, por lo que tu cuenta tendra $115.000.
                                    </p>
                                </Card.Text>
                                <Card.Link href="#">Saber M√°s</Card.Link>
                            </Card.Body>
                        </Card>
                    </div>
                    <div className="col-sm-12 col-lg-6">
                        <Card>
                            <Card.Header>R√©gimen B</Card.Header>
                            <Card.Body>
                                <Card.Text>
                                    <p>
                                        Con este puedes rebajar tu ahorro de tu renta tributable. Esto lo puedes ver reflejado mes a mes en tu liquidaci√≥n de sueldo o una vez al a√±o en la operaci√≥n renta, seg√∫n realices tu aporte.
                                    </p>
                                </Card.Text>
                                <Card.Link href="#">Saber M√°s</Card.Link>
                            </Card.Body>
                        </Card>
                    </div>
                </div>
            </section>

        </div>

    </div>
  )
}
