import TerminosModal from "./TerminosModal";
import React, {useState, useEffect} from "react";
import {Field} from "formik";
import {Wizard, WizardStep, sleep} from "./WizardComponent";
import {Step1Schema, Step2Schema, Step3Schema} from "../utils/validationSchemaWizard";
import { useRouter } from'next/router';
import axios from 'axios';

const APVForm = ({bearer}) => {

    const aacento = "\u00e1";
    const eacento = "\u00e9";
    const iacento = "\u00ed";
    const oacento = "\u00f3";
    const uacento = "\u00fa";

    const router = useRouter();

    const initialValues = {
        nombre: '',
        rut: '',
        correo: '',
        celular: '',
        sueldo: '',
        ahorro: '',
        terminosycondiciones: false
    };

    const [modalShow, setModalShow] = useState(false);
    const handleClose = () => setModalShow(false);
    const handleShow = () => setModalShow(true);

    return (
        <div>
            <section>
                <div>
                    <Wizard
                        initialValues={initialValues}
                        onSubmit={values => {

                            const headers = {
                                "Content-Type": "application/json",
                                "Authorization": `Bearer ${bearer}`
                            };

                            const url = 'https://apvbackendmanager.azurewebsites.net/ApvSimulacion/ingresarsimulacion';

                            const body = {
                                nombre: values.nombre,
                                rut: values.rut,
                                correo: values.correo,
                                celular: values.celular,
                                sueldo: values.sueldo,
                                ahorro: values.ahorro
                            };

                            axios
                                .post(url, body, { headers: headers })
                                .then((response) => {
                                    let data = response.data;
                                    //console.log(data)

                                    if (data.idSimulacion) {
                                        router.push({
                                            pathname: "/resultado",
                                            as: "resultado",
                                            query: {
                                                id: data.idSimulacion,
                                            },
                                        });
                                    }
                                })
                                .catch(e => {
                                    console.log(e);
                                });
                        }}
                    >
                        <WizardStep
                            onSubmit={() => console.log('Step1 onSubmit')}
                            validationSchema={Step1Schema}
                        >
                            <div className="form-group input-wrapper">
                                <label
                                    htmlFor="sueldo"
                                    className={"control-label"}>sueldo
                                </label>
                                <Field name="sueldo"
                                >
                                    {({
                                          field,
                                          form: { touched, errors },
                                          }) => (
                                              <>
                                        <input
                                            type="text"
                                            {...field}
                                            className={
                                                `form-control form-control-lg 
                                            ${touched.sueldo ? (errors.sueldo ? "is-invalid" : "is-valid") : ""}`}
                                            id="sueldo"
                                            aria-describedby="sueldoAyuda"
                                            placeholder="Sueldo Líquido"
                                        />
                                            <small
                                                id="sueldoAyuda"
                                                className={`form-text 
                                                    ${touched.sueldo && errors.sueldo ? "is-invalid" : "" }`}
                                            >
                                                {touched.sueldo && errors.sueldo
                                                    ? errors.sueldo
                                                    : "El sueldo líquido que recibes luego de los descuentos legales."}
                                            </small>
                                        </>
                                    )}
                                </Field>
                            </div>
                            <div className="form-group input-wrapper">
                                <label
                                    htmlFor="ahorro"
                                    className="control-label"
                                >
                                    Monto Ahorro
                                </label>
                                <Field name="ahorro">
                                    {({
                                          field,
                                          form: { touched, errors },
                                      }) => (
                                        <>
                                            <input
                                                {...field}
                                                type="text"
                                                className={`form-control form-control-lg ${
                                                    touched.ahorro ? (errors.ahorro ? "is-invalid" : "is-valid") : ""}`}
                                                id="ahorro"
                                                aria-describedby="ahorroAyuda"
                                                placeholder="Ahorro Mensual"
                                            />
                                            <small
                                                id="ahorroAyuda"
