import TerminosModal from "./TerminosModal";
import React, {useState} from "react";
import {Formik, useFormik, Form, Field, ErrorMessage, FormikValues, formikbag, useFormikContext} from "formik";
import * as Yup from 'yup';
import {rutValidador} from "../utils/validationRut";
import joven from "../public/assets/svg/joven.svg";

const APVForm = () => {

    const [currentStep, setCurrentStep] = useState(0);
    const [lastStep, setLastStep] = useState(2);

    const aacento = "\u00e1";
    const eacento = "\u00e9";
    const iacento = "\u00ed";
    const oacento = "\u00f3";
    const uacento = "\u00fa";


    const initialValues = {
        nombre: '',
        rut: '',
        correo: '',
        celular: '',
        sueldo: '',
        ahorro: '',
        terminosycondiciones: false
    };

    const onSubmit = (values, formikbag) => {
        console.log('Form values', values);
        setTimeout(() => {
            formikbag.setSubmitting(false)
        }, 1000);
    }


    const validationSchema = Yup.object({
        nombre: Yup
            .string()
            .matches(
                /^[A-Za-z \u00F1\u00D1\u00E1\u00E9\u00ED\u00F3\u00FA\u00FC]{3,60}$/,
                `Ingresa tu nombre sin n${uacento}meros ni caracteres.`)
            .required('Ingresa al menos un nombre y apellido.'),
        rut: Yup
            .string()
            .required(`Por favor ingrese un RUT v${aacento}lido sin puntos y con gui${oacento}n.`)
            .test('Rut-validacion',
                `Por favor ingrese un RUT v${aacento}lido sin puntos y con gui${oacento}n.`,
                function (value) {
                    return rutValidador(formik.values.rut);
                }),
        correo: Yup
            .string()
            .email(`Tu correo debe incluir un "@" y un dominio v${aacento}lido.`)
            .required('Por favor ingrese su correo.'),
        celular: Yup
            .string()
            .matches(/^[9]{1}[0-9]{8}$/, `Ingresa un n${uacento}mero de 9 d${iacento}gitos.`)
            .required(`Por favor ingrese su n${uacento}mero de tel${eacento}fono.`),
        sueldo: Yup
            .string()
            .matches(/^[0-9]{6,8}$/, `Ingrese un sueldo l${iacento}quido entre $100.000 y $10.000.000.`)
            .test('Sueldo-validacion', `Ingrese un sueldo l${iacento}quido entre $100.000 y $10.000.000.`, function (value) {
               let amount = formik.values.sueldo;
                return (amount >= 100000 && amount <= 10000000)
            })
            .required('Por favor ingrese su sueldo líquido.'),
        ahorro: Yup
            .string()
            .matches(/^[0-9]+$/, `Ingrese el monto en pesos que desea ahorrar desde $1.000.`)
            .test('Sueldo-validacion', `Ingrese un monto desde $1.000.`, function (value) {
                let amount = formik.values.sueldo;
                return (amount >= 1000 )
            })
            .required('Por favor ingrese el monto que desea ahorrar desde $1.000.'),
        terminosycondiciones: Yup
            .boolean()
            .oneOf([true], `Debe aceptar las condiciones del servicio y la pol${iacento}tica de privacidad.`)

    });

    const [modalShow, setModalShow] = useState(false);
    const handleClose = () => setModalShow(false);
    const handleShow = () => setModalShow(true);

    const Step1Schema = Yup.object({
        sueldo: Yup
            .string()
            .matches(/^[0-9]{6,8}$/, `Ingrese un sueldo l${iacento}quido entre $100.000 y $10.000.000.`)
            .test('Sueldo-validacion', `Ingrese un sueldo l${iacento}quido entre $100.000 y $10.000.000.`, function (value) {
                return (value>= 100000 && value <= 10000000)
            })
            .required('Por favor ingrese su sueldo líquido.'),
        ahorro: Yup
            .string()
            .matches(/^[0-9]+$/, `Ingrese el monto en pesos que desea ahorrar desde $1.000.`)
            .test('Sueldo-validacion', `Ingrese un monto desde $1.000.`, function (value) {
                return (value >= 1000 )
            })
            .required('Por favor ingrese el monto que desea ahorrar desde $1.000.'),
    });

    const Step2Schema = Yup.object({
        nombre: Yup
            .string()
            .matches(
                /^[A-Za-z \u00F1\u00D1\u00E1\u00E9\u00ED\u00F3\u00FA\u00FC]{3,60}$/,
                `Ingresa tu nombre sin n${uacento}meros ni caracteres.`)
            .required('Ingresa al menos un nombre y apellido.'),
        rut: Yup
            .string()
            .required(`Por favor ingrese un RUT v${aacento}lido sin puntos y con gui${oacento}n.`)
            .test('Rut-validacion',
                `Por favor ingrese un RUT v${aacento}lido sin puntos y con gui${oacento}n.`,
                function (value) {
                    return rutValidador(value);
                }),
    });

    const Step3Schema = Yup.object({
        correo: Yup
            .string()
            .email(`Tu correo debe incluir un "@" y un dominio v${aacento}lido.`)
            .required('Por favor ingrese su correo.'),
        celular: Yup
            .string()
            .matches(/^[9]{1}[0-9]{8}$/, `Ingresa un n${uacento}mero de 9 d${iacento}gitos.`)
            .required(`Por favor ingrese su n${uacento}mero de tel${eacento}fono.`),
        terminosycondiciones: Yup
            .boolean()
            .oneOf([true], `Debe aceptar las condiciones del servicio y la pol${iacento}tica de privacidad.`)
    })

    const formik = useFormik({
        initialValues,
        validationSchema,
        onSubmit
    })

    return (
        <div>
            <section>
                <div>
                    <h1>Formik Multistep Wizard</h1>
                    <Wizard
                        initialValues={initialValues}
                        onSubmit={async values =>
                            sleep(300).then(() => console.log('Wizard submit', values))
                        }
                    >
                        <WizardStep
                            onSubmit={() => console.log('Step1 onSubmit')}
                            validationSchema={Step1Schema}
                        >
                            <div className="form-group input-wrapper">
                                <label
                                    htmlFor="sueldo"
                                    className={"control-label"}>sueldo</label>
                                <Field name="sueldo"
                                >
                                    {({
                                          field,
                                          form: { touched, errors },
                                          }) => (
                                              <>
                                        <input
                                            type="text"
                                            {...field}
                                            className={
                                                `form-control form-control-lg 
                                            ${touched.sueldo ? (errors.sueldo ? "is-invalid" : "is-valid") : ""}`}
                                            id="sueldo"
                                            aria-describedby="sueldoAyuda"
                                            placeholder="Sueldo Líquido"
                                        />
                                                  <small
                                                      id="sueldoAyuda"
                                                      className={`form-text 
                        ${touched.sueldo && errors.sueldo ? "is-invalid" : "" }`}
                                                  >
                                                      {touched.sueldo && errors.sueldo
                                                          ? errors.sueldo
                                                          : "El sueldo líquido que recibes luego de los descuentos legales."}
                                                  </small>
                                        </>
                                    )}
                                </Field>
                            </div>
                            <div className="form-group input-wrapper">
                                <label
                                    htmlFor="ahorro"
                                    className="control-label"
                                >
                                    Monto Ahorro
                                </label>
                                <Field name="ahorro">
                                    {({
                                          field,
                                          form: { touched, errors },
                                      }) => (
                                        <>
                                            <input
                                                {...field}
                                                type="text"
                                                className={`form-control form-control-lg ${
                                                    touched.ahorro ? (errors.ahorro ? "is-invalid" : "is-valid") : ""}`}
                                                id="ahorro"
                                                aria-describedby="ahorroAyuda"
                                                placeholder="Ahorro Mensual"
                                            />
                                            <small
                                                id="ahorroAyuda"
                                                className={`form-text ${
                                                    touched.ahorro && errors.ahorro ? "is-invalid" : ""}`}
                                            >
                                                {touched.ahorro && errors.ahorro
                                                    ? errors.ahorro
                                                    : "El monto en pesos que invertirías en tu APV."}
                                            </small>
                                        </>
                                    )}
                                </Field>
                            </div>
                        </WizardStep>
                        <WizardStep
                            onSubmit={() => console.log('Step2 onSubmit')}
                            validationSchema={Step2Schema}
                        >
                            <div className="form-group input-wrapper">
                                <label
                                    htmlFor="nombre"
                                    className="control-label"
                                >
                                    Nombre
                                </label>
                                <Field name="nombre">
                                    {({
                                          field,
                                          form: { touched, errors },
                                      }) => (
                                        <>
                                            <input
                                                {...field}
                                                type="text"
                                                className={`form-control form-control-lg ${
                                                    touched.nombre ? (errors.nombre ? "is-invalid" : "is-valid" ) : ""}`}
                                                id="nombre"
                                                aria-describedby="nombreAyuda"
                                                placeholder="Nombre"
                                            />
                                            <small
                                                id="nombreAyuda"
                                                className={`form-text ${
                                                    touched.nombre && errors.nombre ? "is-invalid" : ""
                                                }`}
                                            >
                                                {touched.nombre && errors.nombre
                                                    ? errors.nombre
                                                    : "Indicanos tu nombre y apellido."}
                                            </small>
                                        </>
                                    )}
                                </Field>
                            </div>
                            <div className="form-group input-wrapper">
                                <label
                                    htmlFor="rut"
                                    className="control-label"
                                >
                                    RUT
                                </label>
                                <Field name="rut">
                                    {({
                                          field,
                                          form: { touched, errors },
                                      }) => (
                                        <>
                                            <input
                                                {...field}
                                                type="text"
                                                className={`form-control form-control-lg ${
                                                    touched.rut ? (errors.rut ? "is-invalid" : "is-valid") : ""}`}
                                                id="rut"
                                                aria-describedby="rutAyuda"
                                                placeholder="RUT"
                                            />
                                            <small
                                                id="rutAyuda"
                                                className={`form-text ${
                                                    touched.rut && errors.rut ? "is-invalid" : ""}`}
                                            >
                                                {touched.rut && errors.rut
                                                    ? errors.rut
                                                    : "Ej. 12345678-9."}
                                            </small>
                                        </>
                                    )}
                                </Field>
                            </div>
                        </WizardStep>
                        <WizardStep
                            onSubmit={() => console.log('Step3 onSubmit')}
                            validationSchema={Step3Schema}
                        >
                            <div className="form-group input-wrapper">
                                <label
                                    htmlFor="correo"
                                    className="control-label"
                                >
                                    Correo
                                </label>
                                <Field name="correo">
                                    {({
                                          field,
                                          form: { touched, errors },
                                      }) => (
                                        <>
                                            <input
                                                {...field}
                                                type="email"
                                                className={`form-control form-control-lg ${
                                                    touched.correo ? (errors.correo ? 'is-invalid' : 'is-valid') : ''
                                                }`}
                                                id="correo"
                                                aria-describedby="correoAyuda"
                                                placeholder="Correo"
                                            />
                                            <small
                                                id="correoAyuda"
                                                className={`form-text ${touched.correo && errors.correo ? "is-invalid" : ""}`}
                                            >
                                                {touched.correo && errors.correo
                                                    ? errors.correo
                                                    : "Ej. aumentatusueldo@afpmodelo.cl"}
                                            </small>
                                        </>
                                    )}
                                </Field>
                            </div>
                            <div className="form-group input-wrapper">
                                <label
                                    htmlFor="celular"
                                    className="control-label"
                                >
                                    Celular
                                </label>
                                <Field name="celular">
                                    {({
                                          field,
                                          form: { touched, errors },
                                      }) => (
                                        <>
                                            <input
                                                {...field}
                                                type="text"
                                                className={`form-control form-control-lg ${
                                                    touched.celular ? (errors.celular ? "is-invalid" : "is-valid") : ""}`}
                                                id="celular"
                                                aria-describedby="celularAyuda"
                                                placeholder="Celular"
                                            />
                                            <small
                                                id="celularAyuda"
                                                className={`form-text ${
                                                    touched.celular && errors.celular ? "is-invalid" : ""}`}
                                            >
                                                {touched.celular && errors.celular ? errors.celular : "Ej. 9111223XX"}
                                            </small>
                                        </>
                                    )}
                                </Field>
                            </div>
                            <div className="form-check">

                                <Field name="terminosycondiciones">
                                    {({
                                          field,
                                          form: { touched, errors },
                                      }) => (
                                        <>
                                            <label
                                                className="form-check-label mt-auto"
                                                htmlFor="terminosycondiciones"
                                            >
                                                <input
                                                    {...field}
                                                    type="checkbox"
                                                    className="form-check-input"
                                                />
                                                <small className="terminosycondiciones">
                                                    Acepto las{" "}
                                                    <a
                                                        onClick={handleShow}
                                                    >
                                                        condiciones del servicio y la pol{iacento}
                                                        tica de privacidad.
                                                    </a>
                                                </small>
                                            </label>
                                            <small
                                                id="terminosAyuda"
                                                className={
                                                    `form-text 
                            ${ touched.terminosycondiciones && errors.terminosycondiciones
                                                        ? 'is-invalid': ''}`}
                                            >
                                                {touched.terminosycondiciones && errors.terminosycondiciones
                                                    ? errors.terminosycondiciones
                                                    : ""}
                                            </small>
                                            <TerminosModal
                                                show={modalShow}
                                                onHide={handleClose}
                                            />
                                        </>
                                    )}
                                </Field>
                            </div>
                        </WizardStep>
                    </Wizard>
                </div>
            </section>
        </div>
    );
};


const sleep = ms => new Promise(resolve => setTimeout(resolve, ms));

// Wizard is a single Formik instance whose children are each page of the
// multi-step form. The form is submitted on each forward transition (can only
// progress with valid input), whereas a backwards step is allowed with
// incomplete data. A snapshot of form state is used as initialValues after each
// transition. Each page has an optional submit handler, and the top-level
// submit is called when the final page is submitted.
const Wizard = ({ children, initialValues, onSubmit }) => {
    const [modalShow, setModalShow] = useState(false);
    const handleClose = () => setModalShow(false);
    const handleShow = () => setModalShow(true);

    const [stepNumber, setStepNumber] = useState(0);
    const steps = React.Children.toArray(children);
    const [snapshot, setSnapshot] = useState(initialValues);

    const step = steps[stepNumber];
    const totalSteps = steps.length;
    const isLastStep = stepNumber === totalSteps - 1;

    const next = values => {
        setSnapshot(values);
        setStepNumber(Math.min(stepNumber + 1, totalSteps - 1));
    };

    const previous = values => {
        setSnapshot(values);
        setStepNumber(Math.max(stepNumber - 1, 0));
    };

    const handleSubmit = async (values, bag) => {
        if (step.props.onSubmit) {
            await step.props.onSubmit(values, bag);
        }
        if (isLastStep) {
            return onSubmit(values, bag);
        } else {
            bag.setTouched({});
            next(values);
        }
    };

    return (
        <div className="row">
            <div className="col-sm-12 col-md-10 bloque form">
                <div className="card formulario">
                    <div className="card-body">
                        <Formik
                            initialValues={snapshot}
                            onSubmit={handleSubmit}
                            validationSchema={step.props.validationSchema}
                        >
                            {(formik) => (
                                <Form
                                >
                                    <p>
                                        Step {stepNumber + 1} of {totalSteps}
                                    </p>
                                    {step}
                                    <div className="d-flex justify-content-center">
                                        <div className="col justify-content-center d-flex">
                                            <button
                                                type="submit"
                                                id="calcular"
                                                className="btn btn-lg btn-block"
                                                disabled={!(formik.isValid && formik.dirty)}
                                            >
                                                {stepNumber >0 ? (stepNumber > 1 ? 'Calcular': 'Continuar') : 'Comenzar'}
                                            </button>
                                        </div>
                                    </div>
                                </Form>
                            )}
                        </Formik>
                    </div>
                </div>
            </div>
            <div className="col-lg-2 ">
                <img src={joven} alt="joven modelo"/>
            </div>
        </div>
    );
};

const WizardStep = ({ children }) => children;


export default APVForm;
