    const { handleChange, handleSubmit, form, errors } = useForm(submit, validate);


  return (
    <div className="container">
      <Head>
        <title>Aumenta tu Sueldo | Solicitud de Contacto | AFP Modelo</title>
        <meta name="description" content="Aumenta tu sueldo líquido, pagando una menor comisión de AFP. Simula tu aumento de sueldo al cambiarte a AFP Modelo." />
        <meta name="robots" content="noindex, follow" />
      </Head>
        <div className="background">
            <section>
                <div>
                    <h1>Formik Multistep Wizard</h1>
                    <Wizard
                        initialValues={{
                            email: '',
                            firstName: '',
                            lastName: '',
                        }}
                        onSubmit={async values =>
                            sleep(300).then(() => console.log('Wizard submit', values))
                        }
                    >
                        <WizardStep
                            onSubmit={() => console.log('Step1 onSubmit')}
                            validationSchema={Yup.object({
                                firstName: Yup.string().required('required'),
                                lastName: Yup.string().required('required'),
                            })}
                        >
                            <div>
                                <label htmlFor="firstName">First Name</label>
                                <Field
                                    autoComplete="given-name"
                                    component="input"
                                    id="firstName"
                                    name="firstName"
                                    placeholder="First Name"
                                    type="text"
                                />
                                <ErrorMessage className="error" component="div" name="firstName" />
                            </div>
                            <div>
                                <label htmlFor="lastName">Last Name</label>
                                <Field
                                    autoComplete="family-name"
                                    component="input"
                                    id="lastName"
                                    name="lastName"
                                    placeholder="Last Name"
                                    type="text"
