import TerminosModal from "./TerminosModal";
import React, {useState} from "react";
import {useFormik} from "formik";
import {eliminarValoresNoEnteros, nombreValidador, sueldoValidador} from "../utils/validations";
import * as Yup from 'yup';

const APVForm = () => {

    const initialValues = {
        nombre: '',
        rut: '',
        correo: '',
        celular: '',
        sueldo: '',
        ahorro: '',
    }

    const onSubmit = values => {
        console.log('Form values', values);
    }

    const validates = values => {
        let errors = {};
        let err ='';

        return errors;

    }

    const validationSchema = Yup.object({
        nombre: Yup.string().required('Required'),
        correo: Yup.string().email('Invalid email format'),
    });


    const formik = useFormik({
       initialValues,
       onSubmit,
       validationSchema
    });

    console.log("visited form: ", formik.touched);
    console.log('errores', formik.errors);





    const [modalShow, setModalShow] = useState(false);
    const [checbox, setChecbox] = useState(false);
    const handleClose = () => setModalShow(false);
    const handleShow = () => setModalShow(true);
    const iacento = "\u00ed";

    function handleInput(e) {
        const value = e.target.checked;
        setChecbox(value);
    }


    return (
        <>
            <form onSubmit={formik.handleSubmit} >
                <div className="form-group input-wrapper">
                    <p>Ingresa tus datos y lo calcularemos para ti:</p>
                    <input
                        type="text"
                        className={`
                            form-control 
                            form-control-lg 
                            ${formik.touched.sueldo ? (formik.errors.sueldo !== "" && formik.errors.sueldo !== undefined  ? "is-invalid" : "is-valid") : ""}`}
                        id="sueldo"
                        name="sueldo"
                        aria-describedby="sueldoAyuda"
                        placeholder="Sueldo Líquido"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.sueldo}
                    />
                    <label
                        htmlFor="sueldo"
                        className={"control-label"}
                    >
                        Sueldo Liquido
                    </label>
                    <small
                        id="sueldoAyuda"
                        className={`form-text ${
                            formik.errors.sueldo !== undefined &&
                            formik.errors.sueldo !== ""
                                ? "is-invalid"
                                : ""
                        }`}
                    >
                        {formik.errors.sueldo !== undefined && formik.errors.sueldo !== ""
                            ? formik.errors.sueldo
                            : "El sueldo líquido que recibes luego de los descuentos legales."}
                    </small>
                </div>

                <div className="form-group input-wrapper">
                    <input
                        type="text"
                        className={`form-control form-control-lg ${
                            formik.errors.ahorro !== undefined
                                ? formik.errors.ahorro !== ""
                                ? "is-invalid"
                                : "is-valid"
                                : ""
                        }`}
                        id="ahorro"
                        name="ahorro"
                        aria-describedby="ahorroAyuda"
                        placeholder="Ahorro Mensual"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.ahorro}
                    />
                    <label
                        htmlFor="ahorro"
                        className={`control-label ${formik.errors.ahorro !== undefined
                            ? formik.errors.ahorro.length > 40
                                ? "label-mobile-error"
                                : ""
                            : ""} ${formik.errors.ahorro !== undefined && formik.errors.ahorro !== ""
                            ? ""
                            : "label-mobile-error"
                        }
                                                    `}
                    >
                        Sueldo Liquido
                    </label>
                    <small
                        id="ahorroAyuda"
                        className={`form-text ${
                            formik.errors.ahorro !== undefined &&
                            formik.errors.ahorro !== ""
                                ? "is-invalid"
                                : ""
                        }`}
                    >
                        {formik.errors.ahorro !== undefined && formik.errors.ahorro !== ""
                            ? formik.errors.ahorro
                            : "El monto en pesos que invertirías en tu APV."}
                    </small>
                </div>
                <div className="form-group input-wrapper">
                    <input
                        type="text"
                        className={`form-control form-control-lg ${
                            formik.errors.nombre !== undefined
                                ? formik.errors.nombre !== ""
                                ? "is-invalid"
                                : "is-valid"
                                : ""
                        }`}
                        id="nombre"
                        name="nombre"
                        aria-describedby="nombreAyuda"
                        placeholder="Nombre"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.nombre}
                    />
                    <label
                        htmlFor="nombre"
                        className={`control-label ${formik.errors.nombre !== undefined
                            ? formik.errors.nombre.length > 40
                                ? "label-mobile-error"
                                : ""
                            : ""}`}
                    >
                        Nombre
                    </label>
                    <small
                        id="nombreAyuda"
                        className={`form-text ${
                            formik.errors.nombre !== undefined &&
                            formik.errors.nombre !== ""
                                ? "is-invalid"
                                : ""
                        }`}
                    >
                        {formik.errors.nombre !== undefined && formik.errors.nombre !== ""
                            ? formik.errors.nombre
                            : "Indicanos tu nombre y apellido."}
                    </small>
                </div>
                <div className="form-group input-wrapper">
                    <input
                        type="text"
                        className={`form-control form-control-lg ${
                            formik.errors.rut !== undefined
                                ? formik.errors.rut !== ""
                                ? "is-invalid"
                                : "is-valid"
                                : ""
                        }`}
                        id="rut"
                        name="rut"
                        aria-describedby="rutAyuda"
                        placeholder="RUT"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.rut}
                    />
                    <label
                        htmlFor="rut"
                        className={`control-label ${formik.errors.rut !== undefined
                            ? formik.errors.rut.length > 40
                                ? "label-mobile-error"
                                : ""
                            : ""}`}
                    >
                        RUT
                    </label>
                    <small
                        id="rutAyuda"
                        className={`form-text ${
                            formik.errors.rut !== undefined && formik.errors.rut !== ""
                                ? "is-invalid"
                                : ""
                        }`}
                    >
                        {formik.errors.rut !== undefined && formik.errors.rut !== ""
                            ? formik.errors.rut
                            : "Ej. 12345678-9."}
                    </small>
                </div>
                <div className="form-group input-wrapper">
                    <input
                        type="email"
                        className={`form-control form-control-lg ${
                            formik.errors.correo !== undefined
                                ? formik.errors.correo !== ""
                                ? "is-invalid"
                                : "is-valid"
                                : ""
                        }`}
                        id="correo"
                        name="correo"
                        aria-describedby="correoAyuda"
                        placeholder="Correo"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.correo}
                    />
                    <label
                        htmlFor="correo"
                        className={`control-label ${formik.errors.correo !== undefined
                            ? formik.errors.correo.length > 40
                                ? "label-mobile-error"
                                : ""
                            : ""}`}
                    >
                        Correo
                    </label>
                    <small
                        id="correoAyuda"
                        className={`form-text ${
                            formik.errors.correo !== undefined &&
                            formik.errors.correo !== ""
                                ? "is-invalid"
                                : ""
                        }`}
                    >
                        {formik.errors.correo !== undefined && formik.errors.correo !== ""
                            ? formik.errors.correo
                            : "Ej. aumentatusueldo@afpmodelo.cl"}
                    </small>
                </div>
                <div className="form-group input-wrapper">
                    <input
                        type="text"
                        className={`form-control form-control-lg ${
                            formik.errors.celular !== undefined
                                ? formik.errors.celular !== ""
                                ? "is-invalid"
                                : "is-valid"
                                : ""
                        }`}
                        id="celular"
                        name="celular"
                        aria-describedby="celularAyuda"
                        placeholder="Celular"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.celular}
                    />
                    <label
                        htmlFor="celular"
                        className={`control-label ${formik.errors.celular !== undefined
                            ? formik.errors.celular.length > 40
                                ? "label-mobile-error"
                                : ""
                            : ""}`}
                    >
                        Celular
                    </label>
                    <small
                        id="celularAyuda"
                        className={`form-text ${
                            formik.errors.celular !== undefined &&
                            formik.errors.celular !== ""
                                ? "is-invalid"
                                : ""
                        }`}
                    >
                        {formik.errors.celular !== undefined &&
                        formik.errors.celular !== ""
                            ? formik.errors.celular
                            : "Ej. 9111223XX"}
                    </small>
                </div>

                <div className="form-check">
                    <label
                        className="form-check-label mt-auto"
                        htmlFor="terminosycondiciones"
                    >
                        <input
                            type="checkbox"
                            name="terminosycondiciones"
                            onChange={handleInput}
                            onBlur={formik.handleBlur}
                            className="form-check-input"

                        />
                        <small className="terminosycondiciones">
                            Acepto las{" "}
                            <a
                                onClick={handleShow}
                            >
                                condiciones del servicio y la pol{iacento}
                                tica de privacidad.
                            </a>
                        </small>
                    </label>
                    {formik.errors.terminosycondiciones !== undefined ? (
                        <small
                            id="terminosAyuda"
                            className={
                                formik.errors.terminosycondiciones !== ""
                                    ? "form-text is-invalid"
                                    : "form-text"
                            }
                        >
                            {formik.errors.terminosycondiciones !== ""
                                ? formik.errors.terminosycondiciones
                                : " "}
                        </small>
                    ) : (
                        " "
                    )}
                </div>
                <div className="d-flex justify-content-center">
                    <div className="col justify-content-center d-flex">
                        <button
                            type="submit"
                            id="calcular"
                            className="btn btn-lg btn-block"
                        >
                            Calcular
                        </button>
                        <TerminosModal
                            show={modalShow}
                            onHide={handleClose}
                        />
                    </div>
                </div>
            </form>
        </>
    );
};

export default APVForm;
