import React, {useState} from "react";
import {Form, Formik} from "formik";
import joven from "../public/assets/svg/joven.svg";
import joven2 from "../public/assets/svg/joven2.svg"

export const sleep = ms => new Promise(resolve => setTimeout(resolve, ms));


export const Wizard = ({ children, initialValues, onSubmit }) => {


    const [stepNumber, setStepNumber] = useState(0);
    const steps = React.Children.toArray(children);
    const [snapshot, setSnapshot] = useState(initialValues);

    const step = steps[stepNumber];
    const totalSteps = steps.length;
    const isLastStep = stepNumber === totalSteps - 1;

    const next = values => {
        setSnapshot(values);
        setStepNumber(Math.min(stepNumber + 1, totalSteps - 1));
    };


    const handleSubmit = async (values, bag) => {
        if (step.props.onSubmit) {
            await step.props.onSubmit(values, bag);
        }
        if (isLastStep) {
            return onSubmit(values, bag);
        } else {
            bag.setTouched({});
            next(values);
        }
    };



    return (
        <>
            <style jsx>
                {`
                .progress-bar{
  display: flex;
  margin: 40px 0;
  user-select: none;
}
.progress-bar .step{
  text-align: center;
  width: 100%;
  position: relative;
}
.progress-bar .step p{
  font-weight: 500;
  font-size: 18px;
  color: #000;
  margin-bottom: 8px;
}
.progress-bar .step .bullet{
  height: 25px;
  width: 25px;
  border: 2px solid #000;
  display: inline-block;
  border-radius: 50%;
  position: relative;
  transition: 0.2s;
  font-weight: 500;
  font-size: 17px;
  line-height: 25px;
}
.progress-bar .step .bullet.active{
  border-color: #d43f8d;
  background: #d43f8d;
}
.progress-bar .step .bullet span{
  position: absolute;
  left: 50%;
  transform: translateX(-50%);
}
.progress-bar .step .bullet.active span{
  display: none;
}
.progress-bar .step .bullet:before,
.progress-bar .step .bullet:after{
  position: absolute;
  content: '';
  bottom: 11px;
  right: -51px;
  height: 3px;
  width: 44px;
  background: #262626;
}
.progress-bar .step .bullet.active:after{
  background: #d43f8d;
  transform: scaleX(0);
  transform-origin: left;
  animation: animate 0.3s linear forwards;
}
@keyframes animate {
  100%{
    transform: scaleX(1);
  }
}
.progress-bar .step:last-child .bullet:before,
.progress-bar .step:last-child .bullet:after{
  display: none;
}
.progress-bar .step p.active{
  color: #d43f8d;
  transition: 0.2s linear;
}
.progress-bar .step .check{
  position: absolute;
  left: 50%;
  top: 70%;
  font-size: 15px;
  transform: translate(-50%, -50%);
  display: none;
}
.progress-bar .step .check.active{
  display: block;
  color: #fff;
}

        `}
            </style>
        <div className="row">
            <div className="col-sm-12 col-md-10 bloque form">
                <div className="card formulario">
                    <div className="progress-bar">
                        <div className="step">
                            <p>
                                Name</p>
                            <div className="bullet">
                                <span>1</span>
                            </div>
                            <div className="check fas fa-check">
                            </div>
                        </div>
                        <div className="step">
                            <p>
                                Contact</p>
                            <div className="bullet">
                                <span>2</span>
                            </div>
                            <div className="check fas fa-check">
                            </div>
                        </div>
                        <div className="step">
                            <p>
                                Birth</p>
                            <div className="bullet">
                                <span>3</span>
                            </div>
                            <div className="check fas fa-check">
                            </div>
                        </div>
                    </div>
                    <div className="card-body">
                        <Formik
                            initialValues={snapshot}
                            onSubmit={handleSubmit}
                            validationSchema={step.props.validationSchema}
                        >
                            {(formik) => (
                                <Form>
                                    <p>
                                        Step {stepNumber + 1} of {totalSteps}
                                    </p>
                                    {step}
                                    <div className="d-flex justify-content-center">
                                        <div className="col justify-content-center d-flex">
                                            <button
                                                type="submit"
                                                id="calcular"
                                                className="btn btn-lg btn-block"
                                                disabled={!(formik.isValid && formik.dirty)}
                                            >
                                                {stepNumber >0 ? (stepNumber > 1 ? 'Calcular': 'Continuar') : 'Comenzar'}
                                            </button>
                                        </div>
                                    </div>
                                </Form>
                            )}
                        </Formik>
                    </div>
                </div>
            </div>
            <div className="col-lg-2 ">
                {stepNumber > 0 ? <img src={joven2} alt="joven modelo 2"/> : <img src={joven} alt="joven modelo"/>}

            </div>
        </div>
            </>
    );
};

export const WizardStep = ({ children }) => children;
