import React, {useState} from "react";
import {Form, Formik} from "formik";
import joven from "../public/assets/svg/joven.svg";
import joven2 from "../public/assets/svg/joven2.svg"

export const sleep = ms => new Promise(resolve => setTimeout(resolve, ms));


export const Wizard = ({ children, initialValues, onSubmit }) => {


    const [stepNumber, setStepNumber] = useState(0);
    const steps = React.Children.toArray(children);
    const [snapshot, setSnapshot] = useState(initialValues);

    const step = steps[stepNumber];
    const totalSteps = steps.length;
    const isLastStep = stepNumber === totalSteps - 1;

    const next = values => {
        setSnapshot(values);
        setStepNumber(Math.min(stepNumber + 1, totalSteps - 1));
    };


    const handleSubmit = async (values, bag) => {
        if (step.props.onSubmit) {
            await step.props.onSubmit(values, bag);
        }
        if (isLastStep) {
            return onSubmit(values, bag);
        } else {
            bag.setTouched({});
            next(values);
        }
    };

    <style jsx>
        {`
        #progressbar {
    margin-bottom: 30px;
    overflow: hidden;
    color: #455A64;
    padding-left: 0px;
    margin-top: 30px
}

#progressbar li {
    list-style-type: none;
    font-size: 13px;
    width: 33.33%;
    float: left;
    position: relative;
    font-weight: 400
}

#progressbar #step1:before {
    content: "1";
    color: #fff
}

#progressbar #step2:before {
    content: "2";
    color: #fff
}

#progressbar #step3:before {
    content: "3";
    color: #fff
}

#progressbar li:before {
    width: 40px;
    height: 40px;
    line-height: 45px;
    display: block;
    font-size: 20px;
    background: #455A64;
    border-radius: 50%;
    margin: auto;
    padding: 0px
}

#progressbar li:after {
    content: '';
    width: 100%;
    height: 2px;
    background: #455A64;
    position: absolute;
    left: 0;
    top: 21px;
    z-index: -1
}

#progressbar li:last-child:after {
    border-top-right-radius: 10px;
    border-bottom-right-radius: 10px;
    position: absolute;
    left: -50%
}

#progressbar li:nth-child(2):after {
    left: -50%
}

#progressbar li:first-child:after {
    border-top-left-radius: 10px;
    border-bottom-left-radius: 10px;
    position: absolute;
    left: 50%
}

#progressbar li:last-child:after {
    border-top-right-radius: 10px;
    border-bottom-right-radius: 10px
}

#progressbar li:first-child:after {
    border-top-left-radius: 10px;
    border-bottom-left-radius: 10px
}

#progressbar li.active:before,
#progressbar li.active:after {
    background: #651FFF
}

        `}
    </style>

    return (
        <div className="row">
            <div className="col-sm-12 col-md-10 bloque form">
                <div className="card formulario">
                    <div>
                        <ul id="progressbar" className="text-center">
                            <li className={`step0 ${stepNumber === 0 ? 'active' : '' }`} id="step1">BUILD</li>
                            <li className={`step0 ${stepNumber === 1 ? 'active' : '' }`} id="step2">DESIGN</li>
                            <li className={`step0 ${stepNumber === 2 ? 'active' : '' }`} id="step3">CONFIRMATION</li>
                        </ul>
                    </div>
                    <div className="card-body">
                        <Formik
                            initialValues={snapshot}
                            onSubmit={handleSubmit}
                            validationSchema={step.props.validationSchema}
                        >
                            {(formik) => (
                                <Form>
                                    <p>
                                        Step {stepNumber + 1} of {totalSteps}
                                    </p>
                                    {step}
                                    <div className="d-flex justify-content-center">
                                        <div className="col justify-content-center d-flex">
                                            <button
                                                type="submit"
                                                id="calcular"
                                                className="btn btn-lg btn-block"
                                                disabled={!(formik.isValid && formik.dirty)}
                                            >
                                                {stepNumber >0 ? (stepNumber > 1 ? 'Calcular': 'Continuar') : 'Comenzar'}
                                            </button>
                                        </div>
                                    </div>
                                </Form>
                            )}
                        </Formik>
                    </div>
                </div>
            </div>
            <div className="col-lg-2 ">
                {stepNumber > 0 ? <img src={joven2} alt="joven modelo 2"/> : <img src={joven} alt="joven modelo"/>}

            </div>
        </div>
    );
};

export const WizardStep = ({ children }) => children;
