import Head from 'next/head'
import ahorro from "../public/assets/svg/ahorro.svg"
import React, { useEffect, useState } from "react";
import TerminosModal from "../components/TerminosModal";
import useForm from "../utils/useForm";
import validate from "../utils/validateForm";
import {Card} from "react-bootstrap";
import ampolleta from "../public/assets/svg/ampolleta.svg";
import joven from "../public/assets/svg/joven.svg"
import {Form, Formik, Field} from 'formik'


const Wizard = ({ children, initialValues, onSubmit }) => {
    const [stepNumber, setStepNumber] = useState(0);
    const steps = React.Children.toArray(children);
    const [snapshot, setSnapshot] = useState(initialValues);

    const step = steps[stepNumber];
    const totalSteps = steps.length;
    const isLastStep = stepNumber === totalSteps - 1;

    const next = values => {
        setSnapshot(values);
        setStepNumber(Math.min(stepNumber + 1, totalSteps - 1));
    };

    const previous = values => {
        setSnapshot(values);
        setStepNumber(Math.max(stepNumber - 1, 0));
    };

    const handleSubmit = async (values, bag) => {
        if (step.props.onSubmit) {
            await step.props.onSubmit(values, bag);
        }
        if (isLastStep) {
            return onSubmit(values, bag);
        } else {
            bag.setTouched({});
            next(values);
        }
    };

    return (
        <Formik
            initialValues={snapshot}
            onSubmit={handleSubmit}
            validationSchema={step.props.validationSchema}
        >
            {formik => (
                <Form>
                    <p>
                        Step {stepNumber + 1} of {totalSteps}
                    </p>
                    {step}
                    <div style={{ display: 'flex' }}>
                        {stepNumber > 0 && (
                            <button onClick={() => previous(formik.values)} type="button">
                                Back
                            </button>
                        )}
                        <div>
                            <button disabled={formik.isSubmitting} type="submit">
                                {isLastStep ? 'Submit' : 'Next'}
                            </button>
                        </div>
                    </div>
                    <Debug />
                </Form>
            )}
        </Formik>
    );
};

const WizardStep = ({ children }) => children;


export default function Home() {

    const [modalShow, setModalShow] = useState(false);
    const [checbox, setChecbox] = useState(false);
    const handleClose = () => setModalShow(false);
    const handleShow = () => setModalShow(true);
    const iacento = "\u00ed";

    if (typeof window !== "undefined") {
        localStorage.setItem("sueldoAnual", 0);
    }


    async function submit() {

        const body = {
            Nombre: form.nombre,
            email: form.correo,
            Rut: form.rut,
            CodAfpOrigen: form.afp,
            Sueldo: eliminarValoresNoEnteros(form.sueldo),
            FonoMovil: form.celular
        };

        console.log(body);
        /**
         const headers = {
            "Content-Type": "application/json"
        };

        axios.post(`Prospectos`, body, { headers: headers }).then((response) => {
            let data = response.data;

            const rutLimpio = form.rut.replace(/[^0-9kK-]/g, "");

            localStorage.setItem("sueldoAnual", data.sueldoAnual);
            localStorage.setItem("sessionId", uuidv4());
            localStorage.setItem("rut", rutLimpio);

            const body_eventos = {
                Sesion_id: localStorage.getItem("sessionId"),
                Evento_id: 0,
                Result_0: parseInt(data.sueldoAnual),
                Result_1: 0,
                Result_2: 0,
                Rut: rutLimpio
            };

            axios
                .post('Eventos', body_eventos, { headers: headers })
                .then((response) => {
                    let data = response.data;

                    if (data.resultado) {
                        window.location.href = "/resultado";
                    }
                })
                .catch(e => {
                    console.log(e);
                });

        });
         **/
    }

    const { handleChange, handleSubmit, form, errors } = useForm(submit, validate);

    function handleInput(e) {
        const value = e.target.checked;
        setChecbox(value);
    }

    useEffect(() => {
        form.esDisabled=validador();
    }, [form]);

    function validador() {
        if (checbox === true && (errors.nombre !== undefined && errors.nombre === "") && (errors.afp !== undefined && errors.afp === "") && (errors.rut !== undefined && errors.rut === "") && (errors.correo !== undefined && errors.correo === "") && (errors.celular !== undefined && errors.celular === "") && (errors.sueldo !== undefined && errors.sueldo === "") ) {
            form.terminosycondiciones = checbox;
            return false;
        } else {
            return true;
        }
    }

  return (
    <div className="container">
      <Head>
        <title>Aumenta tu Sueldo | Solicitud de Contacto | AFP Modelo</title>
        <meta name="description" content="Aumenta tu sueldo l√≠quido, pagando una menor comisi√≥n de AFP. Simula tu aumento de sueldo al cambiarte a AFP Modelo." />
        <meta name="robots" content="noindex, follow" />
      </Head>
        <div className="background">
            <section>
                <div>
                    <h1>Formik Multistep Wizard</h1>
                    <Wizard
                        initialValues={{
                            email: '',
                            firstName: '',
                            lastName: '',
                        }}
                        onSubmit={async values =>
                            sleep(300).then(() => console.log('Wizard submit', values))
                        }
                    >
                        <WizardStep
                            onSubmit={() => console.log('Step1 onSubmit')}
                            validationSchema={Yup.object({
                                firstName: Yup.string().required('required'),
                                lastName: Yup.string().required('required'),
                            })}
                        >
                            <div>
                                <label htmlFor="firstName">First Name</label>
                                <Field
                                    autoComplete="given-name"
                                    component="input"
                                    id="firstName"
                                    name="firstName"
                                    placeholder="First Name"
                                    type="text"
                                />
                                <ErrorMessage className="error" component="div" name="firstName" />
                            </div>
                            <div>
                                <label htmlFor="lastName">Last Name</label>
                                <Field
                                    autoComplete="family-name"
                                    component="input"
                                    id="lastName"
                                    name="lastName"
                                    placeholder="Last Name"
                                    type="text"
                                />
                                <ErrorMessage className="error" component="div" name="lastName" />
                            </div>
                        </WizardStep>
                        <WizardStep
                            onSubmit={() => console.log('Step2 onSubmit')}
                            validationSchema={Yup.object({
                                email: Yup.string()
                                    .email('Invalid email address')
                                    .required('required'),
                            })}
                        >
                            <div>
                                <label htmlFor="email">Email</label>
                                <Field
                                    autoComplete="email"
                                    component="input"
                                    id="email"
                                    name="email"
                                    placeholder="Email"
                                    type="text"
                                />
                                <ErrorMessage className="error" component="div" name="email" />
                            </div>
                        </WizardStep>
                    </Wizard>
                </div>
            </section>
            <section>
                <div className="row w-100 container-padre calculadora">

                    <div className="col-12 d-flex justify-content-center mobile">
                        <div className="container-title">
                            <h1>Conoce cu√°nto</h1>
                            <h1>aumentar√≠a tu sueldo,</h1>
                            <h1>al cambiarte a</h1>
                            <h1>AFP Modelo.</h1>
                            <p>
                                √önete a la AFP con una de las comisiones m√°s bajas del mercado.
                            </p>
                            <br/>
                            <br/>
                            <img src={ahorro} alt="cerditoAhorro" />
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-md-12  desktop flex-column">
                            <img src={ampolleta} alt="pareja modelo" />
                            <h1>¬øPor qu√© abrir un APV?</h1>
                            <p>El APV te permite complementar tus ahorros y/o compensar per√≠odos no cotizados, para mejorar tu pensi√≥n. Dependiendo del r√©gimen tributario que elijas podr√°s recibir un aporte fiscal o rebajar lo ahorrado de tu renta tributable.</p>
                        </div>
                    </div>

                    <div className="row">
                        <div className="col-sm-12 col-md-10">
                            <Card>
                                <Card.Body>
                                  <Formik initialValues={{
                                      'sueldo': '',
                                      'ahorroMensual': '',
                                      'nombreCompleto': '',
                                      'rut': '',
                                      'correo': '',
                                      'celular': ''
                                  }}
                                          onSubmit={()=> {}}>
                                    <Form>
                                        <Field
                                            name="sueldo"
                                            className="form-group input-wrapper"
                                            label="El sueldo l√≠quido que recibes desp√∫es de los descuentos legales."
                                            placeholder="Sueldo l√≠quido"
                                        />


                                    </Form>
                                  </Formik>
                                </Card.Body>
                            </Card>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-sm-12 col-md-10 bloque form">
                            <div className="card formulario">
                                <div className="card-body">
                                    <form onSubmit={handleSubmit}>
                                        <div className="form-group input-wrapper">
                                            <p>Ingresa tus datos y lo calcularemos para ti:</p>
                                            <input
                                                type="text"
                                                className={`form-control form-control-lg ${
                                                    errors.nombre !== undefined
                                                        ? errors.nombre !== ""
                                                        ? "is-invalid"
                                                        : "is-valid"
                                                        : ""
                                                }`}
                                                id="nombre"
                                                name="nombre"
                                                aria-describedby="nombreAyuda"
                                                placeholder="Nombre"
                                                onChange={handleChange}
                                                value={form.nombre}
                                            />
                                            <label
                                                htmlFor="nombre"
                                                className={`control-label ${errors.nombre !== undefined
                                                    ? errors.nombre.length > 40
                                                        ? "label-mobile-error"
                                                        : ""
                                                    : ""}`}
                                            >
                                                Nombre
                                            </label>
                                            <small
                                                id="nombreAyuda"
                                                className={`form-text ${
                                                    errors.nombre !== undefined &&
                                                    errors.nombre !== ""
                                                        ? "is-invalid"
                                                        : ""
                                                }`}
                                            >
                                                {errors.nombre !== undefined && errors.nombre !== ""
                                                    ? errors.nombre
                                                    : "Indicanos tu nombre y apellido."}
                                            </small>
                                        </div>
                                        <div className="form-group input-wrapper">
                                            <input
                                                type="text"
                                                className={`form-control form-control-lg ${
                                                    errors.rut !== undefined
                                                        ? errors.rut !== ""
                                                        ? "is-invalid"
                                                        : "is-valid"
                                                        : ""
                                                }`}
                                                id="rut"
                                                name="rut"
                                                aria-describedby="rutAyuda"
                                                placeholder="RUT"
                                                onChange={handleChange}
                                                value={form.rut}
                                            />
                                            <label
                                                htmlFor="rut"
                                                className={`control-label ${errors.rut !== undefined
                                                    ? errors.rut.length > 40
                                                        ? "label-mobile-error"
                                                        : ""
                                                    : ""}`}
                                            >
                                                RUT
                                            </label>
                                            <small
                                                id="rutAyuda"
                                                className={`form-text ${
                                                    errors.rut !== undefined && errors.rut !== ""
                                                        ? "is-invalid"
                                                        : ""
                                                }`}
                                            >
                                                {errors.rut !== undefined && errors.rut !== ""
                                                    ? errors.rut
                                                    : "Ej. 12345678-9."}
