                        <WizardStep
                            onSubmit={() => console.log('Step3 onSubmit')}
                            validationSchema={Step3Schema}
                        >
                            <div className="form-group input-wrapper">
                                <label
                                    htmlFor="correo"
                                    className="control-label"
                                >
                                    Correo
                                </label>
                                <Field name="correo">
                                    {({
                                          field,
                                          form: { touched, errors },
                                      }) => (
                                        <>
                                            <input
                                                {...field}
                                                type="email"
                                                className={`form-control form-control-lg ${
                                                    touched.correo ? (errors.correo ? 'is-invalid' : 'is-valid') : ''
                                                }`}
                                                id="correo"
                                                aria-describedby="correoAyuda"
                                                placeholder="Correo"
                                            />
                                            <small
                                                id="correoAyuda"
                                                className={`form-text ${touched.correo && errors.correo ? "is-invalid" : ""}`}
                                            >
                                                {touched.correo && errors.correo
                                                    ? errors.correo
                                                    : "Ej. aumentatusueldo@afpmodelo.cl"}
                                            </small>
                                        </>
                                    )}
                                </Field>
                            </div>
                            <div className="form-group input-wrapper">
                                <label
                                    htmlFor="celular"
                                    className="control-label"
                                >
                                    Celular
                                </label>
                                <Field name="celular">
                                    {({
                                          field,
                                          form: { touched, errors },
                                      }) => (
                                        <>
                                            <input
                                                {...field}
                                                type="text"
                                                className={`form-control form-control-lg ${
                                                    touched.celular ? (errors.celular ? "is-invalid" : "is-valid") : ""}`}
                                                id="celular"
                                                aria-describedby="celularAyuda"
                                                placeholder="Celular"
                                            />
                                            <small
                                                id="celularAyuda"
                                                className={`form-text ${
                                                    touched.celular && errors.celular ? "is-invalid" : ""}`}
                                            >
                                                {touched.celular && errors.celular ? errors.celular : "Ej. 9111223XX"}
                                            </small>
                                        </>
                                    )}
                                </Field>
                            </div>
                            <div className="form-check">

                                <Field name="terminosycondiciones">
                                    {({
                                          field,
                                          form: { touched, errors },
                                      }) => (
                                        <>
                                            <label
                                                className="form-check-label mt-auto"
                                                htmlFor="terminosycondiciones"
                                            >
                                                <input
                                                    {...field}
                                                    type="checkbox"
                                                    className="form-check-input"
                                                />
                                                <small className="terminosycondiciones">
                                                    Acepto las{" "}
                                                    <a
                                                        onClick={handleShow}
                                                    >
                                                        condiciones del servicio y la pol{iacento}
                                                        tica de privacidad.
                                                    </a>
                                                </small>
                                            </label>
                                            <small
                                                id="terminosAyuda"
                                                className={
                                                    `form-text 
                            ${ touched.terminosycondiciones && errors.terminosycondiciones
                                                        ? 'is-invalid': ''}`}
                                            >
                                                {touched.terminosycondiciones && errors.terminosycondiciones
                                                    ? errors.terminosycondiciones
                                                    : ""}
                                            </small>
                                            <TerminosModal
                                                show={modalShow}
                                                onHide={handleClose}
                                            />
                                        </>
                                    )}
                                </Field>
                            </div>
                        </WizardStep>
                    </Wizard>
                </div>
            </section>
        </div>
    );
};

export async function getServerSideProps(context) {
    const response = await axios
        .get('https://apvbackendmanager.azurewebsites.net/Token/GenerateToken');
    const bearer = await response.data
    console.log("desde response: ", response);

    if (!bearer) {
        return {
            redirect: {
                destination: '/',
                permanent: false,
            },
        }
    }

    return { props: {bearer} }
}

