import TerminosModal from "./TerminosModal";
import React, {useState} from "react";
import {useFormik} from "formik";

const APVForm = () => {
    const formik = useFormik({
       initialValues: {
           nombre: '',
           rut: '',
           email: '',
           celular: '',
           sueldo: '',
           afp: '',
           terminosycondiciones: ''
       }
    });

    console.log('Form values', formik.values);

    let errors = {

    };

    const [modalShow, setModalShow] = useState(false);
    const [checbox, setChecbox] = useState(false);
    const handleClose = () => setModalShow(false);
    const handleShow = () => setModalShow(true);
    const iacento = "\u00ed";

    function handleInput(e) {
        const value = e.target.checked;
        setChecbox(value);
    }


    return (
        <>
            <form >
                <div className="form-group input-wrapper">
                    <p>Ingresa tus datos y lo calcularemos para ti:</p>
                    <input
                        type="text"
                        className={`form-control form-control-lg ${
                            errors.nombre !== undefined
                                ? errors.nombre !== ""
                                ? "is-invalid"
                                : "is-valid"
                                : ""
                        }`}
                        id="nombre"
                        name="nombre"
                        aria-describedby="nombreAyuda"
                        placeholder="Nombre"
                        onChange={formik.handleChange}
                        value={formik.values.nombre}
                    />
                    <label
                        htmlFor="nombre"
                        className={`control-label ${errors.nombre !== undefined
                            ? errors.nombre.length > 40
                                ? "label-mobile-error"
                                : ""
                            : ""}`}
                    >
                        Nombre
                    </label>
                    <small
                        id="nombreAyuda"
                        className={`form-text ${
                            errors.nombre !== undefined &&
                            errors.nombre !== ""
                                ? "is-invalid"
                                : ""
                        }`}
                    >
                        {errors.nombre !== undefined && errors.nombre !== ""
                            ? errors.nombre
                            : "Indicanos tu nombre y apellido."}
                    </small>
                </div>
                <div className="form-group input-wrapper">
                    <input
                        type="text"
                        className={`form-control form-control-lg ${
                            errors.rut !== undefined
                                ? errors.rut !== ""
                                ? "is-invalid"
                                : "is-valid"
                                : ""
                        }`}
                        id="rut"
                        name="rut"
                        aria-describedby="rutAyuda"
                        placeholder="RUT"
                        onChange={formik.handleChange}
                        value={formik.values.rut}
                    />
                    <label
                        htmlFor="rut"
                        className={`control-label ${errors.rut !== undefined
                            ? errors.rut.length > 40
                                ? "label-mobile-error"
                                : ""
                            : ""}`}
                    >
                        RUT
                    </label>
                    <small
                        id="rutAyuda"
                        className={`form-text ${
                            errors.rut !== undefined && errors.rut !== ""
                                ? "is-invalid"
                                : ""
                        }`}
                    >
                        {errors.rut !== undefined && errors.rut !== ""
                            ? errors.rut
                            : "Ej. 12345678-9."}
                    </small>
                </div>
                <div className="form-group input-wrapper">
                    <input
                        type="email"
                        className={`form-control form-control-lg ${
                            errors.correo !== undefined
                                ? errors.correo !== ""
                                ? "is-invalid"
                                : "is-valid"
                                : ""
                        }`}
                        id="correo"
                        name="correo"
                        aria-describedby="correoAyuda"
                        placeholder="Correo"
                        onChange={formik.handleChange}
                        value={formik.values.correo}
                    />
                    <label
                        htmlFor="correo"
                        className={`control-label ${errors.correo !== undefined
                            ? errors.correo.length > 40
                                ? "label-mobile-error"
                                : ""
                            : ""}`}
                    >
                        Correo
                    </label>
                    <small
                        id="correoAyuda"
                        className={`form-text ${
                            errors.correo !== undefined &&
                            errors.correo !== ""
                                ? "is-invalid"
                                : ""
                        }`}
                    >
                        {errors.correo !== undefined && errors.correo !== ""
                            ? errors.correo
                            : "Ej. aumentatusueldo@afpmodelo.cl"}
                    </small>
                </div>
                <div className="form-group input-wrapper">
                    <input
                        type="text"
                        className={`form-control form-control-lg ${
                            errors.celular !== undefined
                                ? errors.celular !== ""
                                ? "is-invalid"
                                : "is-valid"
                                : ""
                        }`}
                        id="celular"
                        name="celular"
                        aria-describedby="celularAyuda"
                        placeholder="Celular"
                        onChange={formik.handleChange}
                        value={formik.values.celular}
                    />
                    <label
                        htmlFor="celular"
                        className={`control-label ${errors.celular !== undefined
                            ? errors.celular.length > 40
                                ? "label-mobile-error"
                                : ""
                            : ""}`}
                    >
                        Celular
                    </label>
                    <small
                        id="celularAyuda"
                        className={`form-text ${
                            errors.celular !== undefined &&
                            errors.celular !== ""
                                ? "is-invalid"
                                : ""
                        }`}
                    >
                        {errors.celular !== undefined &&
                        errors.celular !== ""
                            ? errors.celular
                            : "Ej. 9111223XX"}
                    </small>
                </div>
                <div className="form-group input-wrapper">
                    <input
                        type="text"
                        className={`form-control form-control-lg ${
                            errors.sueldo !== undefined
                                ? errors.sueldo !== ""
                                ? "is-invalid"
                                : "is-valid"
                                : ""
                        }`}
                        id="sueldo"
                        name="sueldo"
                        aria-describedby="sueldoAyuda"
                        placeholder="Sueldo L√≠quido"
                        onChange={formik.handleChange}
                        value={formik.values.sueldo}
                    />
                    <label
                        htmlFor="sueldo"
                        className={`control-label ${errors.sueldo !== undefined
                            ? errors.sueldo.length > 40
                                ? "label-mobile-error"
                                : ""
                            : ""} ${errors.sueldo !== undefined && errors.sueldo !== ""
                            ? ""
                            : "label-mobile-error"
                        }
                                                    `}
                    >
                        Sueldo Liquido
                    </label>
                    <small
                        id="sueldoAyuda"
                        className={`form-text ${
                            errors.sueldo !== undefined &&
                            errors.sueldo !== ""
                                ? "is-invalid"
                                : ""
                        }`}
                    >
                        {errors.sueldo !== undefined && errors.sueldo !== ""
                            ? errors.sueldo
                            : "El sueldo l√≠quido que recibes luego de los descuentos legales."}
                    </small>
                </div>
                <div className="form-check">
                    <label
                        className="form-check-label mt-auto"
                        htmlFor="terminosycondiciones"
                    >
                        <input
                            type="checkbox"
                            name="terminosycondiciones"
                            onChange={handleInput}
                            className="form-check-input"

                        />
                        <small className="terminosycondiciones">
                            Acepto las{" "}
                            <a
                                onClick={handleShow}
                            >
                                condiciones del servicio y la pol{iacento}
                                tica de privacidad.
                            </a>
                        </small>
                    </label>
                    {errors.terminosycondiciones !== undefined ? (
                        <small
                            id="terminosAyuda"
                            className={
                                errors.terminosycondiciones !== ""
                                    ? "form-text is-invalid"
                                    : "form-text"
                            }
                        >
                            {errors.terminosycondiciones !== ""
                                ? errors.terminosycondiciones
                                : " "}
                        </small>
                    ) : (
                        " "
                    )}
                </div>
                <div className="d-flex justify-content-center">
                    <div className="col justify-content-center d-flex">
                        <button
                            type="submit"
                            id="calcular"
                            className="btn btn-lg btn-block"
                        >
                            Calcular
                        </button>
                        <TerminosModal
                            show={modalShow}
                            onHide={handleClose}
                        />
                    </div>
                </div>
            </form>
        </>
    );
};

export default APVForm;
